//SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.5.0 <0.9.0;

contract Election{
    
   struct Candidate{
       uint id;
       string name;
       uint voteCount;
   }
   
   event update(
       uint indexed _id );
   
   uint public candidateCount;
   
   mapping(uint => Candidate) public candidate;
   
   mapping(address => bool) public voted;
   
   constructor(){
       // the code that initate 
       addCandidate("Modi ji");
       addCandidate("Rajiv Gandhi");
   }
   
   //add Candidate
   function addCandidate(string memory name) private {
       candidateCount++;
       candidate[candidateCount]= Candidate(candidateCount,name,0);
   }
   
   // Voting
   function Vote(uint _id) public{
       require(!voted[msg.sender],'You have already voted');
       require(_id >0 && _id<=candidateCount,"the id doesn't exist");
       
       candidate[_id].voteCount+=1;
       voted[msg.sender]=true;
       emit update(_id);
   }
}
